// Utility functions for parsing Excel files
// Note: In a real implementation, this would use libraries like xlsx or exceljs
// For now, this provides the interface and basic structure

export interface ParsedExcelData {
  sheetName: string;
  headers: string[];
  rows: any[][];
  metadata?: {
    fileType: string;
    lastModified?: Date;
    source?: string;
  };
}

export interface CTEMeasurementData {
  ctpName: string;
  date: Date;
  makeupWater: number;
  undermix: number;
  flowG1?: number;
  temperature?: number;
  pressure?: number;
}

export interface RTSData {
  rtsCode: string;
  rtsName: string;
  measurements: CTEMeasurementData[];
}

export class ExcelParser {
  static async parseFile(file: File): Promise<ParsedExcelData[]> {
    // In a real implementation, this would use a library like xlsx
    // to parse the actual Excel file

    return new Promise((resolve, reject) => {
      const reader = new FileReader();

      reader.onload = (event) => {
        try {
          // Mock parsing - in reality would parse the actual Excel structure
          const mockData: ParsedExcelData[] = [{
            sheetName: 'Sheet1',
            headers: ['–¶–¢–ü', '–†–¢–°', '–î–∞—Ç–∞', '–ü–æ–¥–ø–∏—Ç–∫–∞', '–ü–æ–¥–º–µ—Å'],
            rows: [
              ['–¶–¢–ü-125', '–†–¢–°-1', '2025-01-29', '40.3', '5.2'],
              ['–¶–¢–ü-156', '–†–¢–°-4', '2025-01-29', '45.8', '6.1'],
            ],
            metadata: {
              fileType: file.name.split('.').pop() || 'unknown',
              lastModified: new Date(file.lastModified),
              source: file.name,
            }
          }];

          resolve(mockData);
        } catch (error) {
          reject(new Error(`–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ —Ñ–∞–π–ª–∞: ${error}`));
        }
      };

      reader.onerror = () => {
        reject(new Error('–û—à–∏–±–∫–∞ —á—Ç–µ–Ω–∏—è —Ñ–∞–π–ª–∞'));
      };

      // For Excel files, we would use ArrayBuffer
      reader.readAsArrayBuffer(file);
    });
  }

  static parseMeasurements(data: ParsedExcelData): CTEMeasurementData[] {
    const measurements: CTEMeasurementData[] = [];

    // Find column indices
    const headers = data.headers.map(h => h.toLowerCase());
    const ctpIndex = headers.findIndex(h => h.includes('—Ü—Ç–ø'));
    const dateIndex = headers.findIndex(h => h.includes('–¥–∞—Ç–∞'));
    const makeupIndex = headers.findIndex(h => h.includes('–ø–æ–¥–ø–∏—Ç–∫–∞'));
    const undermixIndex = headers.findIndex(h => h.includes('–ø–æ–¥–º–µ—Å'));

    if (ctpIndex === -1 || dateIndex === -1 || makeupIndex === -1) {
      throw new Error('–ù–µ –Ω–∞–π–¥–µ–Ω—ã –Ω–µ–æ–±—Ö–æ–¥–∏–º—ã–µ –∫–æ–ª–æ–Ω–∫–∏ –≤ —Ñ–∞–π–ª–µ');
    }

    data.rows.forEach((row, index) => {
      try {
        const measurement: CTEMeasurementData = {
          ctpName: String(row[ctpIndex]),
          date: new Date(row[dateIndex]),
          makeupWater: parseFloat(String(row[makeupIndex])),
          undermix: undermixIndex !== -1 ? parseFloat(String(row[undermixIndex])) : 0,
        };

        // Validate data
        if (!measurement.ctpName || isNaN(measurement.makeupWater)) {
          console.warn(`–ü—Ä–æ–ø—É—â–µ–Ω–∞ —Å—Ç—Ä–æ–∫–∞ ${index + 1}: –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ`);
          return;
        }

        measurements.push(measurement);
      } catch (error) {
        console.warn(`–û—à–∏–±–∫–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏ —Å—Ç—Ä–æ–∫–∏ ${index + 1}:`, error);
      }
    });

    return measurements;
  }

  static detectFileType(filename: string): 'measurements' | 'summary' | 'model' | 'unknown' {
    const name = filename.toLowerCase();

    if (name.includes('–æ–¥–ø—É') || name.includes('–ø–æ–∫–∞–∑–∞–Ω–∏—è')) {
      return 'measurements';
    } else if (name.includes('—Å–≤–æ–¥') || name.includes('–≤–µ–¥–æ–º–æ—Å—Ç—å')) {
      return 'summary';
    } else if (name.includes('–º–æ–¥–µ–ª—å') || name.includes('model')) {
      return 'model';
    }

    return 'unknown';
  }

  static validateMeasurementData(data: CTEMeasurementData[]): {
    valid: CTEMeasurementData[];
    errors: string[];
  } {
    const valid: CTEMeasurementData[] = [];
    const errors: string[] = [];

    data.forEach((measurement, index) => {
      if (!measurement.ctpName) {
        errors.push(`–°—Ç—Ä–æ–∫–∞ ${index + 1}: –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç –Ω–∞–∑–≤–∞–Ω–∏–µ –¶–¢–ü`);
        return;
      }

      if (isNaN(measurement.makeupWater) || measurement.makeupWater < 0) {
        errors.push(`–°—Ç—Ä–æ–∫–∞ ${index + 1}: –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ –ø–æ–¥–ø–∏—Ç–∫–∏`);
        return;
      }

      if (measurement.makeupWater > 200) {
        errors.push(`–°—Ç—Ä–æ–∫–∞ ${index + 1}: –ø–æ–¥–æ–∑—Ä–∏—Ç–µ–ª—å–Ω–æ –≤—ã—Å–æ–∫–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ –ø–æ–¥–ø–∏—Ç–∫–∏ (${measurement.makeupWater} —Ç/—á)`);
      }

      valid.push(measurement);
    });

    return { valid, errors };
  }
}

export default ExcelParser;

