import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { TrendChart } from "@/components/charts/trend-chart";
import { StatusBadge } from "@/components/ui/status-badge";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { ArrowDown, ArrowUp, TriangleAlert, Eye } from "lucide-react";
import { cn } from "@/lib/utils";
import type { RTSWithStats } from "@shared/schema";

interface DashboardSummary {
  currentMakeupWater: number;
  ctpRequiringAttention: number;
  ctpInNormal: number;
  outOfControlCount: number;
  rtsStats: RTSWithStats[];
}

export default function Dashboard() {
  const { data: summary, isLoading } = useQuery<DashboardSummary>({
    queryKey: ['/api/dashboard/summary'],
  });

  const { data: yearlyChange } = useQuery<{ change: number }>({
    queryKey: ['/api/trends/overall-change/year'],
  });

  const { data: weeklyChange } = useQuery<{ change: number }>({
    queryKey: ['/api/trends/overall-change/week'],
  });

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {Array.from({ length: 4 }).map((_, i) => (
            <Card key={i}>
              <CardContent className="p-6">
                <Skeleton className="h-8 w-20 mb-2" />
                <Skeleton className="h-4 w-32 mb-2" />
                <Skeleton className="h-4 w-24" />
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (!summary) {
    return (
      <div className="text-center py-12">
        <p className="text-muted-foreground">–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö –¥–∞—à–±–æ—Ä–¥–∞</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="metric-value text-primary" data-testid="metric-makeup-water">
              {summary.currentMakeupWater.toLocaleString()}
            </div>
            <div className="metric-label">–ü–æ–¥–ø–∏—Ç–∫–∞ —Ç/—á (—Ç–µ–∫—É—â–∞—è)</div>
            {yearlyChange && (
              <div className={cn(
                "metric-change",
                yearlyChange.change < 0 ? "positive" : yearlyChange.change > 0 ? "negative" : ""
              )}>
                {yearlyChange.change < 0 && <ArrowDown className="w-3 h-3 mr-1" />}
                {yearlyChange.change > 0 && <ArrowUp className="w-3 h-3 mr-1" />}
                {yearlyChange.change.toFixed(1)} —Ç/—á –∫ –ø—Ä–æ—à–ª–æ–º—É –≥–æ–¥—É
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="metric-value text-yellow-600" data-testid="metric-attention">
              {summary.ctpRequiringAttention}
            </div>
            <div className="metric-label">–¶–¢–ü —Ç—Ä–µ–±—É—é—â–∏—Ö –≤–Ω–∏–º–∞–Ω–∏—è</div>
            <div className="metric-change negative">
              <TriangleAlert className="w-3 h-3 mr-1" />
              {summary.outOfControlCount} –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏—Ö
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="metric-value text-green-600" data-testid="metric-normal">
              {summary.ctpInNormal}%
            </div>
            <div className="metric-label">–¶–¢–ü –≤ –Ω–æ—Ä–º–µ</div>
            {weeklyChange && (
              <div className={cn(
                "metric-change",
                weeklyChange.change < 0 ? "positive" : weeklyChange.change > 0 ? "negative" : ""
              )}>
                {weeklyChange.change < 0 && <ArrowDown className="w-3 h-3 mr-1" />}
                {weeklyChange.change > 0 && <ArrowUp className="w-3 h-3 mr-1" />}
                {Math.abs(weeklyChange.change).toFixed(1)} —Ç/—á –∑–∞ –Ω–µ–¥–µ–ª—é
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="metric-value text-red-600" data-testid="metric-out-of-control">
              {summary.outOfControlCount}
            </div>
            <div className="metric-label">–í—ã—Ö–æ–¥—ã –∑–∞ –≥—Ä–∞–Ω–∏—Ü—ã</div>
            <div className="metric-change negative">
              <ArrowUp className="w-3 h-3 mr-1" />
              –ó–∞ –ø–æ—Å–ª–µ–¥–Ω–∏–µ —Å—É—Ç–∫–∏
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Main Chart */}
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <div>
              <CardTitle>–î–∏–Ω–∞–º–∏–∫–∞ –ø–æ–¥–ø–∏—Ç–∫–∏ –ø–æ –ù–æ–≤–æ—Å–∏–±–∏—Ä—Å–∫—É</CardTitle>
              <CardDescription>–¢–µ–∫—É—â–∏–π –ø–µ—Ä–∏–æ–¥</CardDescription>
            </div>
            <div className="flex gap-2">
              <Button variant="outline" size="sm">–î–µ–Ω—å</Button>
              <Button variant="default" size="sm">–ù–µ–¥–µ–ª—è</Button>
              <Button variant="outline" size="sm">–ú–µ—Å—è—Ü</Button>
              <Button variant="outline" size="sm">–ì–æ–¥</Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="h-[400px]">
            <TrendChart period="week" />
          </div>
        </CardContent>
      </Card>

      {/* Regional Overview */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>–ü—Ä–∞–≤—ã–π –±–µ—Ä–µ–≥</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-[350px]">
              <TrendChart period="week" rtsFilter="right" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader>
            <CardTitle>–õ–µ–≤—ã–π –±–µ—Ä–µ–≥</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-[350px]">
              <TrendChart period="week" rtsFilter="left" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* RTS Breakdown */}
      <Card>
        <CardHeader>
          <CardTitle>–†–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –ø–æ –†–¢–°</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="data-table" data-testid="table-rts-breakdown">
              <thead>
                <tr>
                  <th>–†–¢–°</th>
                  <th>–ü–æ–¥–ø–∏—Ç–∫–∞ —Ç/—á</th>
                  <th>–ò–∑–º–µ–Ω–µ–Ω–∏–µ –∑–∞ –Ω–µ–¥–µ–ª—é</th>
                  <th>–ò–∑–º–µ–Ω–µ–Ω–∏–µ –∑–∞ –≥–æ–¥</th>
                  <th>% –æ—Ç –æ–±—â–µ–π</th>
                  <th>–°—Ç–∞—Ç—É—Å</th>
                  <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                </tr>
              </thead>
              <tbody>
                {(() => {
                  const totalAbsolute = summary.rtsStats.reduce(
                    (sum, rts) => sum + Math.abs(rts.totalMakeupWater), 
                    0
                  );

                  return summary.rtsStats.map((rts) => {
                    const percentage = totalAbsolute > 0 
                      ? ((Math.abs(rts.totalMakeupWater) / totalAbsolute) * 100).toFixed(1)
                      : '0.0';
                    const status = rts.criticalCount > 0 ? 'warning' : 'normal';

                    return (
                      <RTSRow 
                        key={rts.id} 
                        rts={rts} 
                        percentage={percentage} 
                        status={status} 
                      />
                    );
                  });
                })()}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

function RTSRow({ rts, percentage, status }: { 
  rts: RTSWithStats; 
  percentage: string; 
  status: 'normal' | 'warning' | 'critical'; 
}) {
  const { data: weeklyChangeData } = useQuery<{ change: number }>({
    queryKey: [`/api/rts/${rts.id}/weekly-change`],
  });

  const weeklyChange = weeklyChangeData?.change || 0;

  return (
    <tr data-testid={`row-rts-${rts.id}`}>
      <td className="font-semibold">{rts.code} ({rts.name})</td>
      <td className="font-mono">{rts.totalMakeupWater.toFixed(1)}</td>
      <td className={cn(
        weeklyChange < 0 ? "text-green-600" : weeklyChange > 0 ? "text-red-600" : "text-muted-foreground"
      )}>
        {weeklyChange < 0 && <ArrowDown className="w-4 h-4 inline mr-1" />}
        {weeklyChange > 0 && <ArrowUp className="w-4 h-4 inline mr-1" />}
        {weeklyChange.toFixed(1)}
      </td>
      <td className="text-muted-foreground">-</td>
      <td>{percentage}%</td>
      <td>
        <StatusBadge status={status}>
          {status === 'warning' ? '–í–Ω–∏–º–∞–Ω–∏–µ' : '–ù–æ—Ä–º–∞'}
        </StatusBadge>
      </td>
      <td>
        <Button variant="outline" size="sm" data-testid={`button-details-${rts.id}`}>
          <Eye className="w-4 h-4 mr-1" />
          –î–µ—Ç–∞–ª–∏
        </Button>
      </td>
    </tr>
  );
}

