import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { StatusBadge } from "@/components/ui/status-badge";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { CloudUpload, Lightbulb, FileSpreadsheet, File, AlertCircle } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import type { UploadedFile } from "@shared/schema";

export default function DataUpload() {
  const [selectedFiles, setSelectedFiles] = useState<FileList | null>(null);
  const [selectedModelFile, setSelectedModelFile] = useState<File | null>(null);
  const [uploadProgress, setUploadProgress] = useState<Record<string, number>>({});
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: uploadHistory, isLoading } = useQuery<UploadedFile[]>({
    queryKey: ['/api/upload/history'],
  });

  const uploadMutation = useMutation({
    mutationFn: async (files: FileList) => {
      const formData = new FormData();
      Array.from(files).forEach(file => {
        formData.append('files', file);
      });

      const response = await apiRequest('POST', '/api/upload', formData);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "–§–∞–π–ª—ã –∑–∞–≥—Ä—É–∂–µ–Ω—ã —É—Å–ø–µ—à–Ω–æ",
        description: "–û–±—Ä–∞–±–æ—Ç–∫–∞ —Ñ–∞–π–ª–æ–≤ –Ω–∞—á–∞—Ç–∞",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/upload/history'] });
      setSelectedFiles(null);
      setUploadProgress({});
    },
    onError: (error) => {
      toast({
        title: "–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const importModelMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('file', file);

      const response = await apiRequest('POST', '/api/import-model', formData);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "–ú–æ–¥–µ–ª—å –∏–º–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–∞ —É—Å–ø–µ—à–Ω–æ",
        description: `–ò–º–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–æ: ${data.ctpCount} –¶–¢–ü, ${data.measurementCount} –∏–∑–º–µ—Ä–µ–Ω–∏–π, ${data.vyvodCount} –≤—ã–≤–æ–¥–æ–≤`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/ctp'] });
      queryClient.invalidateQueries({ queryKey: ['/api/rts'] });
      setSelectedModelFile(null);
    },
    onError: (error) => {
      toast({
        title: "–û—à–∏–±–∫–∞ –∏–º–ø–æ—Ä—Ç–∞ –º–æ–¥–µ–ª–∏",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleModelFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const extension = file.name.split('.').pop()?.toLowerCase();
      if (['xlsm'].includes(extension || '')) {
        setSelectedModelFile(file);
      } else {
        toast({
          title: "–ù–µ–ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º—ã–π —Ñ–∞–π–ª",
          description: "–î–ª—è –∏–º–ø–æ—Ä—Ç–∞ –º–æ–¥–µ–ª–∏ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç—Å—è —Ç–æ–ª—å–∫–æ —Ñ–∞–π–ª Model_2.5.20.xlsm",
          variant: "destructive",
        });
      }
    }
  };

  const handleImportModel = () => {
    if (selectedModelFile) {
      importModelMutation.mutate(selectedModelFile);
    }
  };

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files && files.length > 0) {
      // Validate file types
      const validFiles = Array.from(files).filter(file => {
        const extension = file.name.split('.').pop()?.toLowerCase();
        return ['xlsx', 'xlsm', 'xlsb'].includes(extension || '');
      });

      if (validFiles.length !== files.length) {
        toast({
          title: "–ù–µ–ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º—ã–µ —Ñ–∞–π–ª—ã",
          description: "–ü–æ–¥–¥–µ—Ä–∂–∏–≤–∞—é—Ç—Å—è —Ç–æ–ª—å–∫–æ —Ñ–∞–π–ª—ã Excel (XLSX, XLSM, XLSB)",
          variant: "destructive",
        });
      }

      if (validFiles.length > 0) {
        const fileList = new DataTransfer();
        validFiles.forEach(file => fileList.items.add(file));
        setSelectedFiles(fileList.files);
      }
    }
  };

  const handleUpload = () => {
    if (selectedFiles && selectedFiles.length > 0) {
      // Simulate progress for UX
      Array.from(selectedFiles).forEach((file, index) => {
        let progress = 0;
        const interval = setInterval(() => {
          progress += Math.random() * 20;
          if (progress >= 100) {
            progress = 100;
            clearInterval(interval);
          }
          setUploadProgress(prev => ({ ...prev, [file.name]: progress }));
        }, 200);
      });

      uploadMutation.mutate(selectedFiles);
    }
  };

  const handleDrop = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const fakeEvent = {
        target: { files }
      } as React.ChangeEvent<HTMLInputElement>;
      handleFileSelect(fakeEvent);
    }
  };

  const handleDragOver = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
  };

  const getFileTypeIcon = (fileType: string) => {
    return <FileSpreadsheet className="w-4 h-4 mr-2" />;
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'completed': return <StatusBadge status="normal">–£—Å–ø–µ—à–Ω–æ</StatusBadge>;
      case 'processing': return <StatusBadge status="info">–û–±—Ä–∞–±–æ—Ç–∫–∞</StatusBadge>;
      case 'error': return <StatusBadge status="critical">–û—à–∏–±–∫–∞</StatusBadge>;
      default: return <StatusBadge status="warning">–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ</StatusBadge>;
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {Array.from({ length: 2 }).map((_, i) => (
            <Card key={i}>
              <CardContent className="p-6">
                <div className="h-48 bg-muted animate-pulse rounded-lg" />
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö</h3>
        <p className="text-muted-foreground text-sm">
          –ò–º–ø–æ—Ä—Ç –¥–∞–Ω–Ω—ã—Ö –∏–∑ Excel —Ñ–∞–π–ª–æ–≤ (XLSM, XLSX, XLSB) —Å –ø–æ–∫–∞–∑–∞–Ω–∏—è–º–∏ –ø—Ä–∏–±–æ—Ä–æ–≤ —É—á–µ—Ç–∞
        </p>
      </div>

      {/* Model Import Section */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center">
            <FileSpreadsheet className="w-5 h-5 mr-2 text-blue-600" />
            –ò–º–ø–æ—Ä—Ç –∞–Ω–∞–ª–∏—Ç–∏—á–µ—Å–∫–æ–π –º–æ–¥–µ–ª–∏ Model_2.5.20.xlsm
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="p-4 bg-blue-50 dark:bg-blue-950/20 border border-blue-200 dark:border-blue-800 rounded-lg">
              <div className="flex items-start space-x-3">
                <Lightbulb className="w-5 h-5 text-blue-600 mt-0.5 flex-shrink-0" />
                <div>
                  <p className="text-sm text-blue-900 dark:text-blue-100 font-medium">
                    –ò–º–ø–æ—Ä—Ç –ø–æ–ª–Ω–æ–π –∞–Ω–∞–ª–∏—Ç–∏—á–µ—Å–∫–æ–π –º–æ–¥–µ–ª–∏
                  </p>
                  <p className="text-xs text-blue-700 dark:text-blue-300 mt-1">
                    –ó–∞–≥—Ä—É–∑–∏—Ç–µ —Ñ–∞–π–ª Model_2.5.20.xlsm –¥–ª—è –∏–º–ø–æ—Ä—Ç–∞ –ø–æ–ª–Ω–æ–≥–æ —Å–ø—Ä–∞–≤–æ—á–Ω–∏–∫–∞ –¶–¢–ü —Å –∏—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏–º–∏ –¥–∞–Ω–Ω—ã–º–∏, –≤—ã–≤–æ–¥–∞–º–∏, –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏—è–º–∏ –∏ —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω–æ–π —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–æ–π.
                  </p>
                </div>
              </div>
            </div>

            <div className="flex items-center gap-4">
              <div className="flex-1">
                <Input
                  id="modelFileInput"
                  type="file"
                  accept=".xlsm"
                  onChange={handleModelFileSelect}
                  data-testid="model-file-input"
                  className="cursor-pointer"
                />
              </div>
              <Button
                onClick={handleImportModel}
                disabled={!selectedModelFile || importModelMutation.isPending}
                data-testid="button-import-model"
                className="bg-blue-600 hover:bg-blue-700"
              >
                {importModelMutation.isPending ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                    –ò–º–ø–æ—Ä—Ç...
                  </>
                ) : (
                  <>
                    <CloudUpload className="w-4 h-4 mr-2" />
                    –ò–º–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –º–æ–¥–µ–ª—å
                  </>
                )}
              </Button>
            </div>

            {selectedModelFile && (
              <div className="flex items-center p-2 bg-muted rounded">
                <FileSpreadsheet className="w-4 h-4 mr-2 text-blue-600" />
                <span className="text-sm">{selectedModelFile.name}</span>
                <span className="text-xs text-muted-foreground ml-2">
                  ({(selectedModelFile.size / 1024 / 1024).toFixed(1)} MB)
                </span>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Upload Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <CloudUpload className="w-5 h-5 mr-2 text-primary" />
              –ó–∞–≥—Ä—É–∑–∫–∞ –Ω–æ–≤—ã—Ö –¥–∞–Ω–Ω—ã—Ö
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div
              className="border-2 border-dashed border-border rounded-lg p-8 text-center cursor-pointer transition-all hover:border-primary hover:bg-primary/5"
              onClick={() => document.getElementById('fileInput')?.click()}
              onDrop={handleDrop}
              onDragOver={handleDragOver}
              data-testid="upload-zone"
            >
              <CloudUpload className="w-12 h-12 mx-auto mb-4 text-primary" />
              <p className="font-semibold mb-2">–ü–µ—Ä–µ—Ç–∞—â–∏—Ç–µ —Ñ–∞–π–ª—ã —Å—é–¥–∞ –∏–ª–∏ –Ω–∞–∂–º–∏—Ç–µ –¥–ª—è –≤—ã–±–æ—Ä–∞</p>
              <p className="text-sm text-muted-foreground">
                –ü–æ–¥–¥–µ—Ä–∂–∏–≤–∞—é—Ç—Å—è —Ñ–æ—Ä–º–∞—Ç—ã: XLSM, XLSX, XLSB
              </p>
            </div>

            <Input
              id="fileInput"
              type="file"
              multiple
              accept=".xlsm,.xlsx,.xlsb"
              className="hidden"
              onChange={handleFileSelect}
              data-testid="file-input"
            />

            {selectedFiles && selectedFiles.length > 0 && (
              <div className="mt-4">
                <h4 className="font-medium mb-2">–í—ã–±—Ä–∞–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã:</h4>
                <div className="space-y-2">
                  {Array.from(selectedFiles).map((file, index) => (
                    <div key={index} className="flex items-center justify-between p-2 bg-muted rounded">
                      <div className="flex items-center">
                        <FileSpreadsheet className="w-4 h-4 mr-2 text-green-600" />
                        <span className="text-sm">{file.name}</span>
                        <span className="text-xs text-muted-foreground ml-2">
                          ({(file.size / 1024 / 1024).toFixed(1)} MB)
                        </span>
                      </div>
                      {uploadProgress[file.name] && (
                        <div className="w-32">
                          <Progress value={uploadProgress[file.name]} className="h-2" />
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            <Button
              className="w-full mt-4"
              onClick={handleUpload}
              disabled={!selectedFiles || selectedFiles.length === 0 || uploadMutation.isPending}
              data-testid="button-upload"
            >
              {uploadMutation.isPending ? (
                <>
                  <div className="loading-spinner mr-2" />
                  –û–±—Ä–∞–±–æ—Ç–∫–∞...
                </>
              ) : (
                <>
                  <CloudUpload className="w-4 h-4 mr-2" />
                  –û–±—Ä–∞–±–æ—Ç–∞—Ç—å –∑–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã
                </>
              )}
            </Button>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Lightbulb className="w-5 h-5 mr-2 text-primary" />
              –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –∑–∞–≥—Ä—É–∑–∫–µ
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-sm space-y-4">
              <p>–°–∏—Å—Ç–µ–º–∞ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç —Å–ª–µ–¥—É—é—â–∏–µ —Ç–∏–ø—ã —Ñ–∞–π–ª–æ–≤:</p>
              <ul className="ml-6 space-y-2">
                <li className="flex items-center">
                  <File className="w-4 h-4 mr-2" />
                  <strong>–ü–æ–∫–∞–∑–∞–Ω–∏—è –û–î–ü–£</strong> - –ø—Ä–∏–±–æ—Ä—ã —É—á–µ—Ç–∞ —Ç–µ–ø–ª–æ–≤–æ–π —ç–Ω–µ—Ä–≥–∏–∏
                </li>
                <li className="flex items-center">
                  <File className="w-4 h-4 mr-2" />
                  <strong>–°–≤–æ–¥ –∂—É—Ä–Ω–∞–ª–∞</strong> - —Å–≤–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –ø–æ –†–¢–°
                </li>
                <li className="flex items-center">
                  <File className="w-4 h-4 mr-2" />
                  <strong>–°–≤–æ–¥–Ω–∞—è –≤–µ–¥–æ–º–æ—Å—Ç—å</strong> - –¥–∞–Ω–Ω—ã–µ –ø–æ –∞–±–æ–Ω–µ–Ω—Ç–∞–º
                </li>
                <li className="flex items-center">
                  <File className="w-4 h-4 mr-2" />
                  <strong>–ê–Ω–∞–ª–∏—Ç–∏—á–µ—Å–∫–∞—è –º–æ–¥–µ–ª—å</strong> - —Ä–∞—Å—á–µ—Ç–Ω—ã–µ –ø–∞—Ä–∞–º–µ—Ç—Ä—ã
                </li>
              </ul>
              <div className="p-3 bg-primary/10 rounded-lg border border-primary/20">
                <div className="flex items-start">
