  const getStatusInfo = () => {
    switch (recommendation.priority) {
      case 'critical': return { status: 'critical' as const, label: '–ö—Ä–∏—Ç–∏—á–Ω–æ' };
      case 'warning': return { status: 'warning' as const, label: '–í–Ω–∏–º–∞–Ω–∏–µ' };
      default: return { status: 'normal' as const, label: '–ù–æ—Ä–º–∞' };
    }
  };

  const statusInfo = getStatusInfo();

  return (
    <Card className={getCardClass()}>
      <CardContent className="p-4">
        <div className="flex justify-between items-start mb-3">
          <div>
            <h5 className="font-semibold mb-1">{recommendation.title}</h5>
            <div className="text-sm text-muted-foreground">{recommendation.description}</div>
          </div>
          <StatusBadge status={statusInfo.status}>
            {statusInfo.label}
          </StatusBadge>
        </div>

        {/* Parameters Display */}
        {mockData && (
          <div className="bg-muted p-4 rounded mb-3">
            <div className="grid grid-cols-4 gap-4 text-sm">
              {mockData.currentMakeupWater && (
                <div>
                  <div className="text-muted-foreground">–¢–µ–∫—É—â–∞—è –ø–æ–¥–ø–∏—Ç–∫–∞</div>
                  <div className={cn(
                    "font-semibold font-mono",
                    recommendation.priority === 'critical' ? 'text-red-600' : 
                    recommendation.priority === 'warning' ? 'text-yellow-600' : 'text-foreground'
                  )}>
                    {mockData.currentMakeupWater} —Ç/—á
                  </div>
                </div>
              )}

              {mockData.ucl && (
                <div>
                  <div className="text-muted-foreground">UCL</div>
                  <div className="font-semibold font-mono">{mockData.ucl} —Ç/—á</div>
                </div>
              )}

              {mockData.excess && (
                <div>
                  <div className="text-muted-foreground">–ü—Ä–µ–≤—ã—à–µ–Ω–∏–µ</div>
                  <div className="font-semibold text-red-600">
                    +{mockData.excess} —Ç/—á ({((mockData.excess / mockData.ucl!) * 100).toFixed(1)}%)
                  </div>
                </div>
              )}

              {mockData.flowG1 && (
                <div>
                  <div className="text-muted-foreground">–†–∞—Å—Ö–æ–¥ G1</div>
                  <div className="font-semibold font-mono">{mockData.flowG1} —Ç/—á</div>
                </div>
              )}

              {mockData.distanceToLimit && (
                <div>
                  <div className="text-muted-foreground">–î–æ –≥—Ä–∞–Ω–∏—Ü—ã</div>
                  <div className="font-semibold text-yellow-600">+{mockData.distanceToLimit} —Ç/—á</div>
                </div>
              )}

              {mockData.trend && (
                <div>
                  <div className="text-muted-foreground">–¢—Ä–µ–Ω–¥</div>
                  <div className="font-semibold text-red-600">‚Üë {mockData.trend}</div>
                </div>
              )}

              {mockData.duration && (
                <div>
                  <div className="text-muted-foreground">–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å</div>
                  <div className="font-semibold">{mockData.duration}</div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Actions */}
        <div className="text-sm mb-3">
          <strong>–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏:</strong>
          <ul className="mt-2 ml-6 space-y-1 leading-relaxed">
            {(() => {
              if (!recommendation.actions) return null;

              try {
                const parsedActions = JSON.parse(recommendation.actions);
                if (Array.isArray(parsedActions)) {
                  return parsedActions.map((action, index) => (
                    <li key={index} className="list-disc">{action}</li>
                  ));
                }
              } catch {
                return <li className="list-disc">{recommendation.actions}</li>;
              }

              return <li className="list-disc">{recommendation.actions}</li>;
            })()}
          </ul>
        </div>

        {/* Action Buttons */}
        <div className="flex gap-2">
          <Button 
            onClick={handleAccept}
            disabled={isAccepted}
            data-testid={`button-accept-${recommendation.id}`}
          >
            <ClipboardCheck className="w-4 h-4 mr-2" />
            {isAccepted ? '–ü—Ä–∏–Ω—è—Ç–æ –≤ —Ä–∞–±–æ—Ç—É' : '–ü—Ä–∏–Ω—è—Ç—å –≤ —Ä–∞–±–æ—Ç—É'}
          </Button>
          <Button variant="outline" onClick={handleShowAnalytics}>
            <BarChart3 className="w-4 h-4 mr-2" />
            –ü–æ–¥—Ä–æ–±–Ω–∞—è –∞–Ω–∞–ª–∏—Ç–∏–∫–∞
          </Button>
          <Button variant="outline" onClick={handleShowHistory}>
            <History className="w-4 h-4 mr-2" />
            –ò—Å—Ç–æ—Ä–∏—è
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

