import { DbStorage } from './db-storage';

async function seed() {
  const storage = new DbStorage();

  console.log('Seeding database...');

  // Create RTS (Regional Thermal Stations)
  const rts1 = await storage.createRTS({
    name: "–¢–≠–¶-5",
    code: "–†–¢–°-1",
    location: "–ü—Ä–∞–≤—ã–π –±–µ—Ä–µ–≥",
  });

  const rts2 = await storage.createRTS({
    name: "–¢–≠–¶-3",
    code: "–†–¢–°-2",
    location: "–õ–µ–≤—ã–π –±–µ—Ä–µ–≥",
  });

  const rts3 = await storage.createRTS({
    name: "–¢–≠–¶-2",
    code: "–†–¢–°-3",
    location: "–ü—Ä–∞–≤—ã–π –±–µ—Ä–µ–≥",
  });

  const rts4 = await storage.createRTS({
    name: "–¢–≠–¶-4",
    code: "–†–¢–°-4",
    location: "–õ–µ–≤—ã–π –±–µ—Ä–µ–≥",
  });

  const rts5 = await storage.createRTS({
    name: "–ö–†–ö",
    code: "–†–¢–°-5",
    location: "–ü—Ä–∞–≤—ã–π –±–µ—Ä–µ–≥",
  });

  console.log('Created 5 RTS');

  // Create Districts
  const district1 = await storage.createDistrict({
    name: "–õ–µ–Ω–∏–Ω—Å–∫–∏–π –º–∏–∫—Ä–æ—Ä–∞–π–æ–Ω",
    rtsId: rts1.id,
  });

  const district2 = await storage.createDistrict({
    name: "–°–æ–≤–µ—Ç—Å–∫–∏–π –º–∏–∫—Ä–æ—Ä–∞–π–æ–Ω",
    rtsId: rts2.id,
  });

  const district3 = await storage.createDistrict({
    name: "–ö–∏—Ä–æ–≤—Å–∫–∏–π –º–∏–∫—Ä–æ—Ä–∞–π–æ–Ω",
    rtsId: rts4.id,
  });

  console.log('Created 3 districts');

  // Create CTPs with control boundaries
  const ctp125 = await storage.createCTP({
    name: "–¶–¢–ü-125",
    code: "125",
    rtsId: rts1.id,
    districtId: district1.id,
    ucl: 36.1,
    cl: 32.5,
    lcl: 28.2,
    hasMeter: true,
    meterStatus: "working",
  });

  const ctp156 = await storage.createCTP({
    name: "–¶–¢–ü-156",
    code: "156",
    rtsId: rts4.id,
    districtId: district3.id,
    ucl: 42.5,
    cl: 38.2,
    lcl: 33.5,
    hasMeter: true,
    meterStatus: "working",
  });

  const ctp234 = await storage.createCTP({
    name: "–¶–¢–ü-234",
    code: "234",
    rtsId: rts2.id,
    districtId: district2.id,
    ucl: 24.8,
    cl: 21.2,
    lcl: 17.9,
    hasMeter: true,
    meterStatus: "working",
  });

  console.log('Created 3 CTPs');

  // Create some historical measurements
  const baseDate = new Date('2025-09-25');
  const ctps = [ctp125, ctp156, ctp234];

  for (let dayOffset = 0; dayOffset < 30; dayOffset++) {
    const measurementDate = new Date(baseDate);
    measurementDate.setDate(baseDate.getDate() + dayOffset);

    for (const ctp of ctps) {
      const baseMakeup = ctp.cl || 30;
      const variation = (Math.random() - 0.5) * 10;

      await storage.createMeasurement({
        ctpId: ctp.id,
        date: measurementDate,
        makeupWater: baseMakeup + variation,
        undermix: (Math.random() - 0.5) * 4,
        flowG1: 45 + (Math.random() - 0.5) * 10,
        temperature: 70 + (Math.random() - 0.5) * 10,
        pressure: 6 + (Math.random() - 0.5) * 2,
      });
    }
  }

  console.log('Created 90 measurements (30 days √ó 3 CTPs)');

  // Calculate and save statistical parameters
  for (const ctp of ctps) {
    const boundaries = await storage.calculateControlBoundaries(ctp.id);

    await storage.updateStatisticalParams({
      ctpId: ctp.id,
      mean: boundaries.cl,
      stdDev: (boundaries.ucl - boundaries.cl) / 3,
      ucl: boundaries.ucl,
      cl: boundaries.cl,
      lcl: boundaries.lcl,
      sampleSize: 30,
    });

    // Update CTP with calculated boundaries
    await storage.updateCTPBoundaries(ctp.id, boundaries);
  }

  console.log('Calculated statistical parameters for all CTPs');

  // Create some recommendations
  await storage.createRecommendation({
    ctpId: ctp125.id,
    type: 'leak_inspection',
    priority: 'critical',
    title: '–¶–¢–ü-125 (–†–¢–°-1, –õ–µ–Ω–∏–Ω—Å–∫–∏–π)',
    description: '–£—Å—Ç–æ–π—á–∏–≤–æ–µ –ø—Ä–µ–≤—ã—à–µ–Ω–∏–µ –ø–æ–¥–ø–∏—Ç–∫–∏ –∑–∞ –ø—Ä–µ–¥–µ–ª–∞–º–∏ –≤–µ—Ä—Ö–Ω–µ–π –∫–æ–Ω—Ç—Ä–æ–ª—å–Ω–æ–π –≥—Ä–∞–Ω–∏—Ü—ã',
    actions: JSON.stringify([
      '–ü—Ä–æ–≤–µ—Å—Ç–∏ –∏–Ω—Å–ø–µ–∫—Ü–∏—é –Ω–∞ –ø—Ä–µ–¥–º–µ—Ç —É—Ç–µ—á–µ–∫ —Ç–µ–ø–ª–æ–Ω–æ—Å–∏—Ç–µ–ª—è',
      '–ü—Ä–æ–≤–µ—Ä–∏—Ç—å —Ä–∞–±–æ—Ç–æ—Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –ø—Ä–∏–±–æ—Ä–æ–≤ —É—á–µ—Ç–∞',
      '–ü—Ä–æ–≤–µ—Ä–∏—Ç—å –ø–∞—Ä–∞–º–µ—Ç—Ä—ã —Ä–∞–±–æ—Ç—ã –ø–æ–¥–ø–∏—Ç–æ—á–Ω—ã—Ö –Ω–∞—Å–æ—Å–æ–≤',
      '–ü—Ä–∏ –≤—ã—è–≤–ª–µ–Ω–∏–∏ —É—Ç–µ—á–µ–∫ - –æ—Ä–≥–∞–Ω–∏–∑–æ–≤–∞—Ç—å —É—Å—Ç—Ä–∞–Ω–µ–Ω–∏–µ'
    ]),
    status: 'new',
  });

  await storage.createRecommendation({
    ctpId: ctp156.id,
    type: 'meter_check',
    priority: 'warning',
    title: '–¶–¢–ü-156 (–†–¢–°-4, –ö–∏—Ä–æ–≤—Å–∫–∏–π)',
    description: '–ü—Ä–µ–≤—ã—à–µ–Ω–∏–µ –ø–æ–¥–ø–∏—Ç–∫–∏ —Å–æ–ø–æ—Å—Ç–∞–≤–∏–º–æ–µ —Å —Ä–∞—Å—Ö–æ–¥–æ–º G1',
    actions: JSON.stringify([
      '–ü–†–ò–û–†–ò–¢–ï–¢: –ü—Ä–æ–≤–µ—Ä–∏—Ç—å –ø—Ä–∏–±–æ—Ä—ã —É—á–µ—Ç–∞ —Ä–∞—Å—Ö–æ–¥–∞ —Ç–µ–ø–ª–æ–Ω–æ—Å–∏—Ç–µ–ª—è',
      '–ü—Ä–æ–≤–µ—Å—Ç–∏ –ø–æ–≤–µ—Ä–∫—É —Å—á–µ—Ç—á–∏–∫–æ–≤',
      '–ü—Ä–æ–≤–µ—Ä–∏—Ç—å –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç—å –ø–µ—Ä–µ–¥–∞—á–∏ –¥–∞–Ω–Ω—ã—Ö –≤ –ê–°–ö–£–≠',
      '–ü—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏ - –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–∏—Ç—å —Ä–∞–±–æ—Ç–æ—Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –ø—Ä–∏–±–æ—Ä–æ–≤ —É—á–µ—Ç–∞'
    ]),
    status: 'new',
  });

  console.log('Created 2 recommendations');

  // Create upload history entry
  await storage.createUploadedFile({
    filename: 'seed_data.xlsx',
    originalName: '–ü–æ–∫–∞–∑–∞–Ω–∏—è –û–î–ü–£ 25.09.2025 - 25.10.2025.xlsx',
    fileType: 'xlsx',
    size: Math.round(1024 * 1024 * 2.8),
    status: 'completed',
    recordsProcessed: 90,
  });

  console.log('Created upload history entry');
  console.log('Database seeded successfully!');
  process.exit(0);
}

seed().catch((error) => {
  console.error('Error seeding database:', error);
  process.exit(1);
});

