import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();

declare module 'http' {
  interface IncomingMessage {
    rawBody: unknown
  }
}
app.use(express.json({
  verify: (req, _res, buf) => {
    req.rawBody = buf;
  }
}));
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import { DbStorage } from "./db-storage";
import { ExcelParser } from "./excel-parser";
import { TrendsCalculator } from "./trends-calculator";
import { insertMeasurementSchema, insertRecommendationSchema, insertUploadedFileSchema } from "@shared/schema";
import { z } from "zod";

const storage = new DbStorage();
const trendsCalculator = new TrendsCalculator(storage);

const upload = multer({ 
  storage: multer.memoryStorage(),
  fileFilter: (req, file, cb) => {
    const allowedTypes = [
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // .xlsx
      'application/vnd.ms-excel.sheet.macroEnabled.12', // .xlsm
      'application/vnd.ms-excel.sheet.binary.macroEnabled.12', // .xlsb
    ];

    if (allowedTypes.includes(file.mimetype) || 
        file.originalname.match(/\.(xlsx|xlsm|xlsb)$/i)) {
      cb(null, true);
    } else {
      cb(new Error('Поддерживаются только файлы Excel (XLSX, XLSM, XLSB)'));
    }
  },
  limits: {
    fileSize: 50 * 1024 * 1024, // 50MB limit
  }
});

export async function registerRoutes(app: Express): Promise<Server> {

  // RTS routes
  app.get("/api/rts", async (req, res) => {
    try {
      const rtsList = await storage.getRTSList();
      res.json(rtsList);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения списка РТС", error });
    }
  });

  app.get("/api/rts/stats", async (req, res) => {
    try {
      const stats = await storage.getRTSWithStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения статистики РТС", error });
    }
  });

  app.get("/api/rts/:id", async (req, res) => {
    try {
      const rts = await storage.getRTSById(req.params.id);
      if (!rts) {
        return res.status(404).json({ message: "РТС не найден" });
      }
      res.json(rts);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения РТС", error });
    }
  });

  // Districts routes
  app.get("/api/districts", async (req, res) => {
    try {
      const { rtsId } = req.query;
      if (rtsId && typeof rtsId === 'string') {
        const districts = await storage.getDistrictsByRTS(rtsId);
        res.json(districts);
      } else {
        res.status(400).json({ message: "Требуется параметр rtsId" });
      }
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения районов", error });
    }
  });

  // CTP routes
  app.get("/api/ctp", async (req, res) => {
    try {
      const { rtsId, districtId, status } = req.query;
      const filters: any = {};

      if (rtsId && typeof rtsId === 'string') filters.rtsId = rtsId;
      if (districtId && typeof districtId === 'string') filters.districtId = districtId;
      if (status && typeof status === 'string') filters.status = status;

      const ctpList = await storage.getCTPList(filters);
      res.json(ctpList);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения списка ЦТП", error });
    }
  });

  app.get("/api/ctp/:id", async (req, res) => {
    try {
      const ctp = await storage.getCTPById(req.params.id);
      if (!ctp) {
        return res.status(404).json({ message: "ЦТП не найден" });
      }
      res.json(ctp);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения ЦТП", error });
    }
  });

  app.get("/api/ctp/:id/measurements", async (req, res) => {
    try {
      const { startDate, endDate } = req.query;
      const start = startDate ? new Date(startDate as string) : undefined;
      const end = endDate ? new Date(endDate as string) : undefined;

      const measurements = await storage.getMeasurements(req.params.id, start, end);
      res.json(measurements);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения измерений", error });
    }
  });

  app.get("/api/ctp/:id/control-chart", async (req, res) => {
    try {
      const { period = "60" } = req.query;
      const periodDays = parseInt(period as string, 10);

      const data = await storage.getControlChartData(req.params.id, periodDays);
      res.json(data);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения данных контрольной карты", error });
    }
  });

  // Measurements routes
  app.post("/api/measurements", async (req, res) => {
    try {
      const validatedData = insertMeasurementSchema.parse(req.body);
      const measurement = await storage.createMeasurement(validatedData);
      res.status(201).json(measurement);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Неверные данные", errors: error.errors });
      }
      res.status(500).json({ message: "Ошибка создания измерения", error });
    }
  });

  // Statistical parameters routes
  app.post("/api/ctp/:id/calculate-boundaries", async (req, res) => {
    try {
      const boundaries = await storage.calculateControlBoundaries(req.params.id);
      await storage.updateCTPBoundaries(req.params.id, boundaries);
      res.json(boundaries);
    } catch (error) {
      res.status(500).json({ message: "Ошибка расчета контрольных границ", error });
    }
  });

  // Recommendations routes
  app.get("/api/recommendations", async (req, res) => {
    try {
      const { ctpId, type, priority, status } = req.query;
      const filters: any = {};

      if (ctpId && typeof ctpId === 'string') filters.ctpId = ctpId;
      if (type && typeof type === 'string') filters.type = type;
      if (priority && typeof priority === 'string') filters.priority = priority;
      if (status && typeof status === 'string') filters.status = status;

      const recommendations = await storage.getRecommendations(filters);
      res.json(recommendations);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения рекомендаций", error });
    }
  });

  app.post("/api/recommendations", async (req, res) => {
    try {
      const validatedData = insertRecommendationSchema.parse(req.body);
      const recommendation = await storage.createRecommendation(validatedData);
      res.status(201).json(recommendation);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Неверные данные", errors: error.errors });
      }
      res.status(500).json({ message: "Ошибка создания рекомендации", error });
    }
  });

  app.patch("/api/recommendations/:id/status", async (req, res) => {
    try {
      const { status } = req.body;
      if (!status || typeof status !== 'string') {
        return res.status(400).json({ message: "Требуется параметр status" });
      }

      await storage.updateRecommendationStatus(req.params.id, status);
      res.json({ message: "Статус рекомендации обновлен" });
    } catch (error) {
      res.status(500).json({ message: "Ошибка обновления статуса рекомендации", error });
    }
  });

  // Analytics and trends routes
  app.get("/api/trends/:period", async (req, res) => {
    try {
      const { period } = req.params;
      const { rtsId } = req.query;

      if (!['day', 'week', 'month', 'year'].includes(period)) {
        return res.status(400).json({ message: "Неверный период" });
      }

      const trends = await storage.getTrendData(period as any, rtsId as string);
      res.json(trends);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения трендов", error });
    }
  });

  app.get("/api/trends/:period/changes", async (req, res) => {
    try {
      const { period } = req.params;

      if (!['week', 'month', 'year'].includes(period)) {
        return res.status(400).json({ message: "Неверный период" });
      }

      const changes = await trendsCalculator.getTopChanges(period as any);
      res.json(changes);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения изменений трендов", error });
    }
  });

  app.get("/api/trends/:period/rts-stats", async (req, res) => {
    try {
      const { period } = req.params;

      if (!['week', 'month', 'year'].includes(period)) {
        return res.status(400).json({ message: "Неверный период" });
      }

      const stats = await trendsCalculator.getRTSStats(period as any);
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения статистики РТС", error });
    }
  });

  app.get("/api/ctp/:id/weekly-change", async (req, res) => {
    try {
      const change = await trendsCalculator.calculateCTPWeeklyChange(req.params.id);
      res.json({ change });
    } catch (error) {
      res.status(500).json({ message: "Ошибка расчета недельного изменения", error });
    }
  });

  app.get("/api/rts/:id/weekly-change", async (req, res) => {
    try {
      const change = await trendsCalculator.calculateRTSWeeklyChange(req.params.id);
      res.json({ change });
    } catch (error) {
      res.status(500).json({ message: "Ошибка расчета недельного изменения РТС", error });
    }
  });

  // File upload routes
  app.post("/api/upload", upload.array('files'), async (req, res) => {
    try {
      if (!req.files || !Array.isArray(req.files)) {
        return res.status(400).json({ message: "Файлы не были загружены" });
      }

      const uploadResults = [];

      for (const file of req.files) {
        const fileData = insertUploadedFileSchema.parse({
          filename: file.filename || file.originalname,
          originalName: file.originalname,
          fileType: file.originalname.split('.').pop()?.toLowerCase() || 'unknown',
          size: file.size,
          status: 'processing',
        });

        const uploadedFile = await storage.createUploadedFile(fileData);
        uploadResults.push(uploadedFile);

        // Process Excel file asynchronously
        processExcelFile(file.buffer, file.originalname, uploadedFile.id).catch(error => {
          console.error('Error processing file:', error);
          storage.updateFileStatus(uploadedFile.id, 'error', 0, [String(error)]);
        });
      }

      res.json({ 
        message: `Загружено ${uploadResults.length} файлов`,
        files: uploadResults 
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Неверные данные файла", errors: error.errors });
      }
      res.status(500).json({ message: "Ошибка загрузки файла", error });
    }
  });

  async function processExcelFile(buffer: Buffer, filename: string, uploadId: string) {
    try {
      const parsedSheets = await ExcelParser.parseFile(buffer, filename);
      let totalRecords = 0;
      const errors: string[] = [];

      for (const sheet of parsedSheets) {
        try {
          const measurements = ExcelParser.parseMeasurements(sheet);
          const { valid, errors: validationErrors } = ExcelParser.validateMeasurementData(measurements);

          errors.push(...validationErrors);

          // Save measurements to database
          for (const measurement of valid) {
            try {
              // Find or create CTP by name or code
              const ctpList = await storage.getCTPList();
              let ctp = ctpList.find(c => 
                c.name === measurement.ctpName || 
                c.code === measurement.ctpCode
              );

              if (!ctp) {
                // Extract code from name if possible (e.g., "ЦТП-125" -> "125")
                const codeMatch = measurement.ctpName.match(/\d+/);
                const code = measurement.ctpCode || codeMatch?.[0] || measurement.ctpName;

                // Use default RTS and district for now
                const rtsList = await storage.getRTSList();
                const defaultRTS = rtsList[0];
                const districts = await storage.getDistrictsByRTS(defaultRTS.id);
                const defaultDistrict = districts[0];

                const newCtp = await storage.createCTP({
                  name: measurement.ctpName,
                  code: code,
                  rtsId: defaultRTS.id,
                  districtId: defaultDistrict.id,
                  hasMeter: true,
                  meterStatus: 'working',
                });

                // Refetch with details
                ctp = await storage.getCTPById(newCtp.id);
              }

              if (!ctp) {
                throw new Error(`Не удалось создать или найти ЦТП ${measurement.ctpName}`);
              }

              // Save measurement
              await storage.createMeasurement({
                ctpId: ctp.id,
                date: measurement.date,
                makeupWater: measurement.makeupWater,
                undermix: measurement.undermix,
                flowG1: measurement.flowG1,
                temperature: measurement.temperature,
                pressure: measurement.pressure,
              });

              totalRecords++;
            } catch (error) {
              errors.push(`Ошибка сохранения данных для ${measurement.ctpName}: ${error}`);
            }
          }
        } catch (error) {
          errors.push(`Ошибка обработки листа ${sheet.sheetName}: ${error}`);
        }
      }

      // Update file status
      if (errors.length > 0 && totalRecords === 0) {
        await storage.updateFileStatus(uploadId, 'error', totalRecords, errors);
      } else {
        await storage.updateFileStatus(uploadId, 'completed', totalRecords, errors.length > 0 ? errors : undefined);
      }

      // Calculate control boundaries for affected CTPs
      const ctpList = await storage.getCTPList();
      for (const ctp of ctpList) {
        const measurements = await storage.getMeasurements(ctp.id);
        if (measurements.length >= 10) {
          const boundaries = await storage.calculateControlBoundaries(ctp.id);
          await storage.updateCTPBoundaries(ctp.id, boundaries);
          await storage.updateStatisticalParams({
            ctpId: ctp.id,
            mean: boundaries.cl,
            stdDev: (boundaries.ucl - boundaries.cl) / 3,
            ucl: boundaries.ucl,
            cl: boundaries.cl,
            lcl: boundaries.lcl,
            sampleSize: measurements.length,
          });
        }
      }
    } catch (error) {
      await storage.updateFileStatus(uploadId, 'error', 0, [String(error)]);
    }
  }

  app.get("/api/upload/history", async (req, res) => {
    try {
      const history = await storage.getUploadHistory();
      res.json(history);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения истории загрузок", error });
    }
  });

  // Dashboard summary route
  app.get("/api/dashboard/summary", async (req, res) => {
    try {
      const rtsList = await storage.getRTSWithStats();
      const recommendations = await storage.getRecommendations({ status: 'open' });
      const criticalRecommendations = recommendations.filter(r => r.priority === 'critical');

      const totalMakeupWater = rtsList.reduce((sum, rts) => sum + rts.totalMakeupWater, 0);
      const totalCTP = rtsList.reduce((sum, rts) => sum + rts.ctpCount, 0);
      const totalCritical = rtsList.reduce((sum, rts) => sum + rts.criticalCount, 0);
      const totalNormal = rtsList.reduce((sum, rts) => sum + rts.normalCount, 0);

      const summary = {
        currentMakeupWater: Math.round(totalMakeupWater),
        ctpRequiringAttention: totalCritical + recommendations.filter(r => r.priority === 'warning').length,
        ctpInNormal: Math.round((totalNormal / totalCTP) * 100),
        outOfControlCount: criticalRecommendations.length,
        rtsStats: rtsList,
      };

      res.json(summary);
    } catch (error) {
      res.status(500).json({ message: "Ошибка получения сводки дашборда", error });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type RTS, type InsertRTS, type District, type InsertDistrict, type CTP, type InsertCTP, 
         type Measurement, type InsertMeasurement, type StatisticalParams, type InsertStatisticalParams,
         type Recommendation, type InsertRecommendation, type UploadedFile, type InsertUploadedFile,
         type CTPWithDetails, type RTSWithStats, type TrendData, type ControlChartData } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // RTS methods
  getRTSList(): Promise<RTS[]>;
  getRTSById(id: string): Promise<RTS | undefined>;
  createRTS(rts: InsertRTS): Promise<RTS>;
  getRTSWithStats(): Promise<RTSWithStats[]>;

  // District methods
  getDistrictsByRTS(rtsId: string): Promise<District[]>;
  createDistrict(district: InsertDistrict): Promise<District>;

  // CTP methods
  getCTPList(filters?: { rtsId?: string; districtId?: string; status?: string }): Promise<CTPWithDetails[]>;
  getCTPById(id: string): Promise<CTPWithDetails | undefined>;
  createCTP(ctp: InsertCTP): Promise<CTP>;
  updateCTPBoundaries(ctpId: string, boundaries: { ucl: number; cl: number; lcl: number }): Promise<void>;

  // Measurements methods
  getMeasurements(ctpId: string, startDate?: Date, endDate?: Date): Promise<Measurement[]>;
  createMeasurement(measurement: InsertMeasurement): Promise<Measurement>;
  getLatestMeasurements(): Promise<Map<string, Measurement>>;

  // Statistical methods
  getStatisticalParams(ctpId: string): Promise<StatisticalParams | undefined>;
  updateStatisticalParams(params: InsertStatisticalParams): Promise<StatisticalParams>;
  calculateControlBoundaries(ctpId: string): Promise<{ ucl: number; cl: number; lcl: number }>;

  // Recommendations methods
  getRecommendations(filters?: { ctpId?: string; type?: string; priority?: string; status?: string }): Promise<Recommendation[]>;
  createRecommendation(recommendation: InsertRecommendation): Promise<Recommendation>;
  updateRecommendationStatus(id: string, status: string): Promise<void>;

  // Trends and Analytics
  getTrendData(period: 'day' | 'week' | 'month' | 'year', rtsId?: string): Promise<TrendData[]>;
  getControlChartData(ctpId: string, period: number): Promise<ControlChartData[]>;

  // File upload
  createUploadedFile(file: InsertUploadedFile): Promise<UploadedFile>;
  getUploadHistory(): Promise<UploadedFile[]>;
  updateFileStatus(id: string, status: string, recordsProcessed?: number, errors?: any[]): Promise<void>;
}

export class MemStorage implements IStorage {
  private rtsData: Map<string, RTS> = new Map();
  private districtsData: Map<string, District> = new Map();
  private ctpData: Map<string, CTP> = new Map();
  private measurementsData: Map<string, Measurement> = new Map();
  private statisticalParamsData: Map<string, StatisticalParams> = new Map();
  private recommendationsData: Map<string, Recommendation> = new Map();
  private uploadedFilesData: Map<string, UploadedFile> = new Map();

  constructor() {
    this.initializeTestData();
  }

  private initializeTestData(): void {
    // Initialize with sample data structure based on the documents
    const rts1: RTS = {
      id: "rts-1",
      name: "ТЭЦ-5",
      code: "РТС-1",
      location: "Правый берег",
      createdAt: new Date(),
    };

    const rts2: RTS = {
      id: "rts-2",
      name: "ТЭЦ-3",
      code: "РТС-2",
      location: "Левый берег",
      createdAt: new Date(),
    };

    const rts3: RTS = {
      id: "rts-3",
      name: "ТЭЦ-2",
      code: "РТС-3",
      location: "Правый берег",
      createdAt: new Date(),
    };

    const rts4: RTS = {
      id: "rts-4",
      name: "ТЭЦ-4",
      code: "РТС-4",
      location: "Левый берег",
      createdAt: new Date(),
    };

    const rts5: RTS = {
      id: "rts-5",
      name: "КРК",
      code: "РТС-5",
      location: "Правый берег",
      createdAt: new Date(),
    };

    [rts1, rts2, rts3, rts4, rts5].forEach(rts => this.rtsData.set(rts.id, rts));

    // Districts
    const districts = [
      { id: "district-1", name: "Ленинский", rtsId: "rts-1", createdAt: new Date() },
      { id: "district-2", name: "Советский", rtsId: "rts-2", createdAt: new Date() },
      { id: "district-3", name: "Кировский", rtsId: "rts-4", createdAt: new Date() },
    ];
    districts.forEach(district => this.districtsData.set(district.id, district));

    // Sample CTPs with control boundaries
    const ctps: CTP[] = [
      {
        id: "ctp-125",
        name: "ЦТП-125",
        code: "125",
        rtsId: "rts-1",
        districtId: "district-1",
        ucl: 36.1,
        cl: 32.5,
        lcl: 28.2,
        hasMeter: true,
        meterStatus: "working",
        createdAt: new Date(),
      },
      {
        id: "ctp-156",
        name: "ЦТП-156",
        code: "156",
        rtsId: "rts-4",
        districtId: "district-3",
        ucl: 42.5,
        cl: 38.2,
        lcl: 33.5,
        hasMeter: true,
        meterStatus: "working",
        createdAt: new Date(),
      },
      {
        id: "ctp-234",
        name: "ЦТП-234",
        code: "234",
        rtsId: "rts-2",
        districtId: "district-2",
        ucl: 24.8,
        cl: 21.2,
        lcl: 17.9,
        hasMeter: true,
        meterStatus: "working",
        createdAt: new Date(),
      },
    ];
    ctps.forEach(ctp => this.ctpData.set(ctp.id, ctp));
  }

  async getRTSList(): Promise<RTS[]> {
    return Array.from(this.rtsData.values());
  }

  async getRTSById(id: string): Promise<RTS | undefined> {
    return this.rtsData.get(id);
  }

  async createRTS(rts: InsertRTS): Promise<RTS> {
    const id = randomUUID();
    const newRTS: RTS = { ...rts, id, createdAt: new Date() };
    this.rtsData.set(id, newRTS);
    return newRTS;
  }

  async getRTSWithStats(): Promise<RTSWithStats[]> {
    const rtsList = await this.getRTSList();
    const stats: RTSWithStats[] = [];

    for (const rts of rtsList) {
      const ctpList = Array.from(this.ctpData.values()).filter(ctp => ctp.rtsId === rts.id);
      const latestMeasurements = await this.getLatestMeasurements();

      let totalMakeupWater = 0;
      let criticalCount = 0;
      let warningCount = 0;
      let normalCount = 0;

      for (const ctp of ctpList) {
        const measurement = latestMeasurements.get(ctp.id);
        if (measurement) {
          totalMakeupWater += measurement.makeupWater;

          if (measurement.makeupWater > (ctp.ucl || 0)) {
            criticalCount++;
          } else if (measurement.makeupWater > (ctp.cl || 0)) {
            warningCount++;
          } else {
            normalCount++;
          }
        }
      }

      stats.push({
        ...rts,
        totalMakeupWater,
        ctpCount: ctpList.length,
        criticalCount,
        warningCount,
        normalCount,
      });
    }

    return stats;
  }

  async getDistrictsByRTS(rtsId: string): Promise<District[]> {
    return Array.from(this.districtsData.values()).filter(district => district.rtsId === rtsId);
  }

  async createDistrict(district: InsertDistrict): Promise<District> {
    const id = randomUUID();
    const newDistrict: District = { 
      ...district, 
      id, 
      rtsId: district.rtsId ?? null,
      createdAt: new Date() 
    };
    this.districtsData.set(id, newDistrict);
    return newDistrict;
  }

  async getCTPList(filters?: { rtsId?: string; districtId?: string; status?: string }): Promise<CTPWithDetails[]> {
    let ctpList = Array.from(this.ctpData.values());

    if (filters?.rtsId) {
      ctpList = ctpList.filter(ctp => ctp.rtsId === filters.rtsId);
    }
    if (filters?.districtId) {
      ctpList = ctpList.filter(ctp => ctp.districtId === filters.districtId);
    }

    const latestMeasurements = await this.getLatestMeasurements();

    const result: CTPWithDetails[] = [];
    for (const ctp of ctpList) {
      const rts = this.rtsData.get(ctp.rtsId!);
      const district = this.districtsData.get(ctp.districtId!);
      const latestMeasurement = latestMeasurements.get(ctp.id);
      const statisticalParams = this.statisticalParamsData.get(ctp.id);
      const recommendations = Array.from(this.recommendationsData.values())
        .filter(rec => rec.ctpId === ctp.id);

      if (rts && district) {
        result.push({
          ...ctp,
          rts,
          district,
          latestMeasurement,
          statisticalParams,
          recommendations,
        });
      }
    }

    return result;
  }

  async getCTPById(id: string): Promise<CTPWithDetails | undefined> {
    const ctp = this.ctpData.get(id);
    if (!ctp) return undefined;

    const rts = this.rtsData.get(ctp.rtsId!);
    const district = this.districtsData.get(ctp.districtId!);
    const latestMeasurements = await this.getLatestMeasurements();
    const latestMeasurement = latestMeasurements.get(ctp.id);
    const statisticalParams = this.statisticalParamsData.get(ctp.id);
    const recommendations = Array.from(this.recommendationsData.values())
      .filter(rec => rec.ctpId === ctp.id);

    if (rts && district) {
      return {
        ...ctp,
        rts,
        district,
        latestMeasurement,
        statisticalParams,
        recommendations,
      };
    }

    return undefined;
  }

  async createCTP(ctp: InsertCTP): Promise<CTP> {
    const id = randomUUID();
    const newCTP: CTP = { 
      ...ctp, 
      id, 
      rtsId: ctp.rtsId ?? null,
      districtId: ctp.districtId ?? null,
      ucl: ctp.ucl ?? null,
      cl: ctp.cl ?? null,
      lcl: ctp.lcl ?? null,
      hasMeter: ctp.hasMeter ?? null,
      meterStatus: ctp.meterStatus ?? null,
      createdAt: new Date() 
    };
    this.ctpData.set(id, newCTP);
    return newCTP;
  }

  async updateCTPBoundaries(ctpId: string, boundaries: { ucl: number; cl: number; lcl: number }): Promise<void> {
    const ctp = this.ctpData.get(ctpId);
    if (ctp) {
      this.ctpData.set(ctpId, { ...ctp, ...boundaries });
    }
  }

  async getMeasurements(ctpId: string, startDate?: Date, endDate?: Date): Promise<Measurement[]> {
    let measurements = Array.from(this.measurementsData.values())
      .filter(measurement => measurement.ctpId === ctpId);

    if (startDate) {
      measurements = measurements.filter(m => m.date >= startDate);
    }
    if (endDate) {
      measurements = measurements.filter(m => m.date <= endDate);
    }

    return measurements.sort((a, b) => a.date.getTime() - b.date.getTime());
  }

  async createMeasurement(measurement: InsertMeasurement): Promise<Measurement> {
    const id = randomUUID();
    const newMeasurement: Measurement = { 
      ...measurement, 
      id, 
      undermix: measurement.undermix ?? null,
      flowG1: measurement.flowG1 ?? null,
      temperature: measurement.temperature ?? null,
      pressure: measurement.pressure ?? null,
      createdAt: new Date() 
    };
    this.measurementsData.set(id, newMeasurement);
    return newMeasurement;
  }

  async getLatestMeasurements(): Promise<Map<string, Measurement>> {
    const latest = new Map<string, Measurement>();

    const measurementsArray = Array.from(this.measurementsData.values());
    for (const measurement of measurementsArray) {
      const current = latest.get(measurement.ctpId);
      if (!current || measurement.date > current.date) {
        latest.set(measurement.ctpId, measurement);
      }
    }

    return latest;
  }

  async getStatisticalParams(ctpId: string): Promise<StatisticalParams | undefined> {
    return Array.from(this.statisticalParamsData.values())
      .find(params => params.ctpId === ctpId);
  }

  async updateStatisticalParams(params: InsertStatisticalParams): Promise<StatisticalParams> {
    const existing = Array.from(this.statisticalParamsData.values())
      .find(p => p.ctpId === params.ctpId);

    if (existing) {
      const updated = { ...existing, ...params, calculatedAt: new Date() };
      this.statisticalParamsData.set(existing.id, updated);
      return updated;
    } else {
      const id = randomUUID();
      const newParams: StatisticalParams = { ...params, id, calculatedAt: new Date() };
      this.statisticalParamsData.set(id, newParams);
      return newParams;
    }
  }

  async calculateControlBoundaries(ctpId: string): Promise<{ ucl: number; cl: number; lcl: number }> {
    const measurements = await this.getMeasurements(ctpId);

    if (measurements.length < 10) {
      throw new Error("Недостаточно данных для расчета контрольных границ");
    }

    const values = measurements.map(m => m.makeupWater);
    const mean = values.reduce((sum, val) => sum + val, 0) / values.length;
    const variance = values.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / (values.length - 1);
    const stdDev = Math.sqrt(variance);

    // Shewhart control chart with 3-sigma limits
    const ucl = mean + 3 * stdDev;
    const lcl = Math.max(0, mean - 3 * stdDev); // Ensure LCL is not negative

    return { ucl, cl: mean, lcl };
  }

  async getRecommendations(filters?: { ctpId?: string; type?: string; priority?: string; status?: string }): Promise<Recommendation[]> {
    let recommendations = Array.from(this.recommendationsData.values());

    if (filters?.ctpId) {
      recommendations = recommendations.filter(rec => rec.ctpId === filters.ctpId);
    }
    if (filters?.type) {
      recommendations = recommendations.filter(rec => rec.type === filters.type);
    }
    if (filters?.priority) {
      recommendations = recommendations.filter(rec => rec.priority === filters.priority);
    }
    if (filters?.status) {
      recommendations = recommendations.filter(rec => rec.status === filters.status);
    }

    return recommendations.sort((a, b) => {
      const aTime = a.createdAt?.getTime() ?? 0;
      const bTime = b.createdAt?.getTime() ?? 0;
      return bTime - aTime;
    });
  }

  async createRecommendation(recommendation: InsertRecommendation): Promise<Recommendation> {
    const id = randomUUID();
    const newRecommendation: Recommendation = { 
      ...recommendation, 
      id, 
      status: recommendation.status ?? null,
      actions: recommendation.actions ?? null,
      createdAt: new Date(), 
      updatedAt: new Date() 
    };
    this.recommendationsData.set(id, newRecommendation);
    return newRecommendation;
  }

  async updateRecommendationStatus(id: string, status: string): Promise<void> {
    const recommendation = this.recommendationsData.get(id);
    if (recommendation) {
      this.recommendationsData.set(id, { ...recommendation, status, updatedAt: new Date() });
    }
  }

  async getTrendData(period: 'day' | 'week' | 'month' | 'year', rtsId?: string): Promise<TrendData[]> {
    // Generate trend data based on period
    const now = new Date();
    const data: TrendData[] = [];
    let days = 7;

    switch (period) {
      case 'day':
        days = 1;
        break;
      case 'week':
        days = 7;
        break;
      case 'month':
        days = 30;
        break;
      case 'year':
        days = 365;
        break;
    }

    for (let i = days; i >= 0; i--) {
      const date = new Date(now);
      date.setDate(date.getDate() - i);

      // Simulate trend data - in real implementation, this would query actual measurements
      const baseValue = 2750;
      const variation = (Math.random() - 0.5) * 200;

      data.push({
        date: date.toISOString().split('T')[0],
        value: Math.round(baseValue + variation),
        rtsId,
      });
    }

    return data;
  }

  async getControlChartData(ctpId: string, period: number): Promise<ControlChartData[]> {
    const measurements = await this.getMeasurements(ctpId);
    const ctp = this.ctpData.get(ctpId);

    if (!ctp) return [];

    const ucl = ctp.ucl || 0;
    const cl = ctp.cl || 0;
    const lcl = ctp.lcl || 0;

    return measurements.slice(-period).map(measurement => {
      const isAboveUCL = measurement.makeupWater > ucl;
      const isBelowLCL = measurement.makeupWater < lcl;

      return {
        date: measurement.date.toISOString().split('T')[0],
        value: measurement.makeupWater,
        ucl,
        cl,
        lcl,
        isOutOfControl: isAboveUCL || isBelowLCL,
        controlType: isAboveUCL ? 'upper' : isBelowLCL ? 'lower' : 'normal',
      };
    });
  }

  async createUploadedFile(file: InsertUploadedFile): Promise<UploadedFile> {
    const id = randomUUID();
    const newFile: UploadedFile = { 
      ...file, 
      id, 
      status: file.status ?? null,
      recordsProcessed: file.recordsProcessed ?? null,
      errors: file.errors ?? null,
      uploadedAt: new Date() 
    };
    this.uploadedFilesData.set(id, newFile);
    return newFile;
  }

  async getUploadHistory(): Promise<UploadedFile[]> {
    return Array.from(this.uploadedFilesData.values())
      .sort((a, b) => {
        const aTime = a.uploadedAt?.getTime() ?? 0;
        const bTime = b.uploadedAt?.getTime() ?? 0;
        return bTime - aTime;
      });
  }

  async updateFileStatus(id: string, status: string, recordsProcessed?: number, errors?: any[]): Promise<void> {
    const file = this.uploadedFilesData.get(id);
    if (file) {
      this.uploadedFilesData.set(id, { 
        ...file, 
        status, 
        recordsProcessed: recordsProcessed || file.recordsProcessed,
        errors: errors || file.errors 
      });
    }
  }
}

export const storage = new MemStorage();

