import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { TreeNode } from "@/components/tree/tree-node";
import { cn } from "@/lib/utils";
import type { RTSWithStats, CTPWithDetails } from "@shared/schema";

const treeTabs = [
  { key: 'short', label: 'Горизонт: Сутки - Неделя' },
  { key: 'long', label: 'Горизонт: Неделя - Год' },
];

export default function Tree() {
  const [activeTab, setActiveTab] = useState<string>('short');

  const { data: rtsStats, isLoading: loadingRTS } = useQuery<RTSWithStats[]>({
    queryKey: ['/api/rts/stats'],
  });

  const { data: ctpList, isLoading: loadingCTP } = useQuery<CTPWithDetails[]>({
    queryKey: ['/api/ctp'],
  });

  if (loadingRTS || loadingCTP) {
    return (
      <div className="space-y-6">
        <div className="h-16 bg-muted animate-pulse rounded-lg" />
        <div className="h-96 bg-muted animate-pulse rounded-lg" />
      </div>
    );
  }

  if (!rtsStats || !ctpList) {
    return (
      <div className="text-center py-12">
        <p className="text-muted-foreground">Ошибка загрузки данных дерева подпитки</p>
      </div>
    );
  }

  const totalMakeupWater = rtsStats.reduce((sum, rts) => sum + rts.totalMakeupWater, 0);
  const totalWeeklyChange = -48.9; // This would come from trend data in real implementation

  // Group CTP by RTS
  const ctpByRTS = ctpList.reduce((acc, ctp) => {
    if (!acc[ctp.rtsId!]) {
      acc[ctp.rtsId!] = [];
    }
    acc[ctp.rtsId!].push(ctp);
    return acc;
  }, {} as Record<string, CTPWithDetails[]>);

  // Group CTP by district within each RTS
  const ctpByDistrict = (rtsId: string) => {
    const rtsCTPs = ctpByRTS[rtsId] || [];
    return rtsCTPs.reduce((acc, ctp) => {
      if (!acc[ctp.districtId!]) {
        acc[ctp.districtId!] = [];
      }
      acc[ctp.districtId!].push(ctp);
      return acc;
    }, {} as Record<string, CTPWithDetails[]>);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">Дерево подпитки ЦТП с рекомендациями</h3>
        <p className="text-muted-foreground text-sm">
          Иерархическая структура подпитки по РТС, микрорайонам и ЦТП
        </p>
      </div>

      {/* Tab Container */}
      <div className="tab-container">
        {treeTabs.map((tab) => (
          <div
            key={tab.key}
            className={cn("tab", activeTab === tab.key && "active")}
            onClick={() => setActiveTab(tab.key)}
            data-testid={`tab-${tab.key}`}
          >
            {tab.label}
          </div>
        ))}
      </div>

      {/* Tree Structure */}
      <Card>
        <CardContent className="p-6">
          {/* Root Node - Новосибирск */}
          <TreeNode
            id="novosibirsk"
            icon="city"
            name="Новосибирск"
            makeupWater={totalMakeupWater}
            weeklyChange={totalWeeklyChange}
            status="normal"
            isRoot
            data-testid="tree-root"
          >
            {/* RTS Level */}
            {rtsStats.map((rts) => {
              const rtsCTPs = ctpByRTS[rts.id] || [];
              const rtsWeeklyChange = -12.5; // This would be calculated from actual trend data
              const rtsStatus = rts.criticalCount > 0 ? 'warning' : 'normal';
              const districts = ctpByDistrict(rts.id);

              return (
                <TreeNode
                  key={rts.id}
                  id={rts.id}
                  icon="building"
                  name={`${rts.code} (${rts.name})`}
                  makeupWater={rts.totalMakeupWater}
                  weeklyChange={rtsWeeklyChange}
                  status={rtsStatus}
                  data-testid={`tree-rts-${rts.id}`}
                >
                  {/* District Level */}
                  {Object.entries(districts).map(([districtId, districtCTPs]) => {
                    if (districtCTPs.length === 0) return null;

                    const district = districtCTPs[0].district;
                    const districtMakeupWater = districtCTPs.reduce(
                      (sum, ctp) => sum + (ctp.latestMeasurement?.makeupWater || 0), 0
                    );
                    const districtWeeklyChange = -4.2; // Would be calculated from trend data
                    const hasWarning = districtCTPs.some(ctp => 
                      ctp.recommendations.some(r => r.priority === 'warning' || r.priority === 'critical')
                    );

                    return (
                      <TreeNode
                        key={districtId}
                        id={districtId}
                        icon="map"
                        name={`${district.name} микрорайон`}
                        makeupWater={districtMakeupWater}
                        weeklyChange={districtWeeklyChange}
                        status={hasWarning ? 'warning' : 'normal'}
                        data-testid={`tree-district-${districtId}`}
                      >
                        {/* CTP Level */}
                        {districtCTPs.map((ctp) => {
                          const measurement = ctp.latestMeasurement;
                          const weeklyChange = Math.random() * 10 - 5; // Mock weekly change

                          let status: 'normal' | 'warning' | 'critical' = 'normal';
                          let actionType: string | undefined;

                          const criticalRec = ctp.recommendations.find(r => r.priority === 'critical');
                          const warningRec = ctp.recommendations.find(r => r.priority === 'warning');

                          if (criticalRec) {
                            status = 'critical';
                            actionType = criticalRec.type === 'inspection' ? 'Инспекция утечек' : 'Проверка приборов';
                          } else if (warningRec) {
                            status = 'warning';
                            actionType = 'Мониторинг';
                          }

                          return (
                            <TreeNode
                              key={ctp.id}
                              id={ctp.id}
                              icon="thermometer"
                              name={ctp.name}
                              makeupWater={measurement?.makeupWater || 0}
                              weeklyChange={weeklyChange}
                              status={status}
                              actionType={actionType}
                              isLeaf
                              data-testid={`tree-ctp-${ctp.id}`}
                            />
                          );
                        })}
                      </TreeNode>
                    );
                  })}
                </TreeNode>
              );
            })}
          </TreeNode>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/trends.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { TrendChart } from "@/components/charts/trend-chart";
import { cn } from "@/lib/utils";
import { Skeleton } from "@/components/ui/skeleton";

const trendTabs = [
  { key: 'week', label: 'Неделя' },
  { key: 'month', label: 'Месяц' },
  { key: 'year', label: 'Год' },
];

interface TrendChange {
  ctpId: string;
  ctpName: string;
  change: number;
  changePercent: number;
}

interface RTSTrendChange {
  rtsId: string;
  rtsName: string;
  change: number;
  changePercent: number;
}

export default function Trends() {
  const [activeTab, setActiveTab] = useState<string>('week');

  const { data: changes, isLoading: changesLoading } = useQuery<{
    increases: TrendChange[];
    decreases: TrendChange[];
  }>({
    queryKey: ['/api/trends', activeTab, 'changes'],
    enabled: !!activeTab,
  });

  const { data: rtsStats, isLoading: rtsLoading } = useQuery<RTSTrendChange[]>({
    queryKey: ['/api/trends', activeTab, 'rts-stats'],
    enabled: !!activeTab,
  });

  const formatValue = (change: number) => {
    const sign = change >= 0 ? '+' : '';
    return `${sign}${change.toFixed(1)} т/ч`;
  };

  const isLoading = changesLoading || rtsLoading;

  return (
    <div className="space-y-6">
      {/* Tab Container */}
      <div className="tab-container">
        {trendTabs.map((tab) => (
          <div
            key={tab.key}
            className={cn("tab", activeTab === tab.key && "active")}
            onClick={() => setActiveTab(tab.key)}
            data-testid={`tab-${tab.key}`}
          >
            {tab.label}
          </div>
        ))}
      </div>

      {/* Main Chart */}
      <Card>
        <CardHeader>
          <CardTitle>
            Тренды ЦТП «{trendTabs.find(t => t.key === activeTab)?.label}» в т/ч
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="h-[450px]" data-testid="trends-chart">
            <TrendChart period={activeTab as 'week' | 'month' | 'year'} />
          </div>
        </CardContent>
      </Card>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Top Increase */}
        <Card>
          <CardHeader>
            <CardTitle className="text-base font-semibold">Топ рост подпитки</CardTitle>
          </CardHeader>
          <CardContent className="text-sm">
            {isLoading ? (
              <div className="space-y-2">
                <Skeleton className="h-8 w-full" />
                <Skeleton className="h-8 w-full" />
                <Skeleton className="h-8 w-full" />
              </div>
            ) : changes?.increases && changes.increases.length > 0 ? (
              changes.increases.map((item, index) => (
                <div 
                  key={item.ctpId}
                  className="flex justify-between py-2 border-b border-border last:border-0"
                  data-testid={`increase-${index}`}
                >
                  <span>{item.ctpName}</span>
                  <span className="font-semibold text-red-600">{formatValue(item.change)}</span>
                </div>
              ))
            ) : (
              <p className="text-muted-foreground">Нет данных</p>
            )}
          </CardContent>
        </Card>

        {/* Top Decrease */}
        <Card>
          <CardHeader>
            <CardTitle className="text-base font-semibold">Топ снижение подпитки</CardTitle>
          </CardHeader>
          <CardContent className="text-sm">
            {isLoading ? (
              <div className="space-y-2">
                <Skeleton className="h-8 w-full" />
                <Skeleton className="h-8 w-full" />
                <Skeleton className="h-8 w-full" />
              </div>
            ) : changes?.decreases && changes.decreases.length > 0 ? (
              changes.decreases.map((item, index) => (
                <div 
                  key={item.ctpId}
                  className="flex justify-between py-2 border-b border-border last:border-0"
                  data-testid={`decrease-${index}`}
                >
                  <span>{item.ctpName}</span>
                  <span className="font-semibold text-green-600">{formatValue(item.change)}</span>
                </div>
              ))
            ) : (
              <p className="text-muted-foreground">Нет данных</p>
            )}
          </CardContent>
        </Card>

        {/* RTS Statistics */}
        <Card>
          <CardHeader>
            <CardTitle className="text-base font-semibold">Статистика по РТС</CardTitle>
          </CardHeader>
          <CardContent className="text-sm">
            {isLoading ? (
              <div className="space-y-2">
                <Skeleton className="h-8 w-full" />
                <Skeleton className="h-8 w-full" />
                <Skeleton className="h-8 w-full" />
              </div>
            ) : rtsStats && rtsStats.length > 0 ? (
              rtsStats.map((item, index) => (
                <div 
                  key={item.rtsId}
                  className="flex justify-between py-2 border-b border-border last:border-0"
                  data-testid={`rts-stat-${index}`}
                >
                  <span>{item.rtsName}</span>
                  <span className={cn(
                    "font-semibold",
                    item.change >= 0 ? "text-red-600" : "text-green-600"
                  )}>{formatValue(item.change)}</span>
                </div>
              ))
            ) : (
              <p className="text-muted-foreground">Нет данных</p>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/charts/control-cha.content:
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, ReferenceLine } from "recharts";
import type { ControlChartData } from "@shared/schema";

interface ControlChartProps {
  data: ControlChartData[];
}

export function ControlChart({ data }: ControlChartProps) {
  if (!data || data.length === 0) {
    return (
      <div className="h-full flex items-center justify-center text-muted-foreground">
        Данные для контрольной карты не найдены
      </div>
    );
  }

  const formatTooltip = (value: any, name: string) => {
    if (typeof value === 'number') {
      return [`${value.toFixed(2)} т/ч`, name];
    }
    return [value, name];
  };

  const formatLabel = (label: string) => {
    return new Date(label).toLocaleDateString('ru-RU');
  };

  // Get the control limits from the first data point
  const ucl = data[0]?.ucl || 0;
  const cl = data[0]?.cl || 0;
  const lcl = data[0]?.lcl || 0;

  return (
    <ResponsiveContainer width="100%" height="100%">
      <LineChart data={data}>
        <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
        <XAxis 
          dataKey="date" 
          stroke="hsl(var(--muted-foreground))"
          fontSize={12}
          tickFormatter={(value) => new Date(value).toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit' })}
        />
        <YAxis 
          stroke="hsl(var(--muted-foreground))"
          fontSize={12}
          domain={['dataMin - 5', 'dataMax + 5']}
        />
        <Tooltip 
          formatter={formatTooltip}
          labelFormatter={formatLabel}
          contentStyle={{
            backgroundColor: 'hsl(var(--card))',
            border: '1px solid hsl(var(--border))',
            borderRadius: 'var(--radius)',
          }}
        />
        <Legend />

        {/* Control Lines */}
        <ReferenceLine 
          y={ucl} 
          stroke="#dc2626" 
          strokeDasharray="5 5" 
          strokeWidth={2}
          label={{ value: "UCL", position: "right" }}
        />
        <ReferenceLine 
          y={cl} 
          stroke="#16a34a" 
          strokeDasharray="5 5" 
          strokeWidth={2}
          label={{ value: "CL", position: "right" }}
        />
        <ReferenceLine 
          y={lcl} 
          stroke="#2563eb" 
          strokeDasharray="5 5" 
          strokeWidth={2}
          label={{ value: "LCL", position: "right" }}
        />

        {/* Data Line */}
        <Line 
          type="monotone" 
          dataKey="value" 
          stroke="hsl(var(--primary))" 
          strokeWidth={2}
          dot={(props: any) => {
            const isOutOfControl = data[props.index]?.isOutOfControl;
            const controlType = data[props.index]?.controlType;

            if (isOutOfControl) {
              return (
                <circle 
                  cx={props.cx} 
                  cy={props.cy} 
                  r={5}
                  fill={controlType === 'upper' ? '#dc2626' : controlType === 'lower' ? '#2563eb' : '#16a34a'}
                  stroke="#fff"
                  strokeWidth={2}
                />
              );
            }
            return <circle cx={props.cx} cy={props.cy} r={3} fill="hsl(var(--primary))" />;
          }}
          activeDot={{ r: 6, stroke: "hsl(var(--primary))", strokeWidth: 2, fill: "#fff" }}
          name="Подпитка"
        />
      </LineChart>
    </ResponsiveContainer>
  );
}

