client/src/components/charts/trend-chart.content:
import { useQuery } from "@tanstack/react-query";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { Skeleton } from "@/components/ui/skeleton";
import type { TrendData } from "@shared/schema";

interface TrendChartProps {
  period: 'day' | 'week' | 'month' | 'year';
  rtsFilter?: string;
  ctpId?: string;
}

export function TrendChart({ period, rtsFilter, ctpId }: TrendChartProps) {
  const { data: trendData, isLoading } = useQuery<TrendData[]>({
    queryKey: ['/api/trends', period, rtsFilter, ctpId],
  });

  if (isLoading) {
    return <Skeleton className="h-full w-full" />;
  }

  if (!trendData || trendData.length === 0) {
    return (
      <div className="h-full flex items-center justify-center text-muted-foreground">
        Данные не найдены
      </div>
    );
  }

  const chartData = trendData.map(item => ({
    date: new Date(item.date).toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit'
    }),
    value: item.value,
  }));

  return (
    <ResponsiveContainer width="100%" height="100%">
      <LineChart data={chartData}>
        <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
        <XAxis 
          dataKey="date" 
          stroke="hsl(var(--muted-foreground))"
          fontSize={12}
        />
        <YAxis 
          stroke="hsl(var(--muted-foreground))"
          fontSize={12}
        />
        <Tooltip 
          contentStyle={{
            backgroundColor: 'hsl(var(--card))',
            border: '1px solid hsl(var(--border))',
            borderRadius: 'var(--radius)',
          }}
        />
        <Line 
          type="monotone" 
          dataKey="value" 
          stroke="hsl(var(--primary))" 
          strokeWidth={2}
          dot={{ r: 4 }}
          activeDot={{ r: 6 }}
        />
      </LineChart>
    </ResponsiveContainer>
  );
}

client/src/components/recommendations/re.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { StatusBadge } from "@/components/ui/status-badge";
import { ClipboardCheck, BarChart3, History } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";
import type { Recommendation } from "@shared/schema";

interface RecommendationCardProps {
  recommendation: Recommendation;
  mockData?: {
    currentMakeupWater?: number;
    ucl?: number;
    lcl?: number;
    cl?: number;
    flowG1?: number;
    excess?: number;
    distanceToLimit?: number;
    duration?: string;
    trend?: string;
  };
}

export function RecommendationCard({ recommendation, mockData }: RecommendationCardProps) {
  const [isAccepted, setIsAccepted] = useState(false);
  const { toast } = useToast();

  const handleAccept = () => {
    setIsAccepted(true);
    toast({
      title: "Рекомендация принята в работу",
      description: `${recommendation.title} добавлена в план работ`,
    });
  };

  const handleShowAnalytics = () => {
    toast({
      title: "Аналитика",
      description: "Подробная аналитика будет открыта в отдельном окне",
    });
  };

  const handleShowHistory = () => {
    toast({
      title: "История",
      description: "История изменений параметров ЦТП",
    });
  };

  const getCardClass = () => {
    switch (recommendation.priority) {
      case 'critical': return 'recommendation-card meter-check border-l-red-500';
      case 'warning': return 'recommendation-card inspection border-l-yellow-500';
      default: return 'recommendation-card normal border-l-green-500';
    }
  };

  const getStatusInfo = () => {
    switch (recommendation.priority) {
      case 'critical': return { status: 'critical' as const, label: 'Критично' };
      case 'warning': return { status: 'warning' as const, label: 'Внимание' };
      default: return { status: 'normal' as const, label: 'Норма' };
    }
  };

  const statusInfo = getStatusInfo();

  return (
    <Card className={getCardClass()}>
      <CardContent className="p-4">
        <div className="flex justify-between items-start mb-3">
          <div>
            <h5 className="font-semibold mb-1">{recommendation.title}</h5>
            <div className="text-sm text-muted-foreground">{recommendation.description}</div>
          </div>
          <StatusBadge status={statusInfo.status}>
            {statusInfo.label}
          </StatusBadge>
        </div>

        {/* Parameters Display */}
        {mockData && (
          <div className="bg-muted p-4 rounded mb-3">
            <div className="grid grid-cols-4 gap-4 text-sm">
              {mockData.currentMakeupWater && (
                <div>
                  <div className="text-muted-foreground">Текущая подпитка</div>
                  <div className={cn(
                    "font-semibold font-mono",
                    recommendation.priority === 'critical' ? 'text-red-600' : 
                    recommendation.priority === 'warning' ? 'text-yellow-600' : 'text-foreground'
                  )}>
                    {mockData.currentMakeupWater} т/ч
                  </div>
                </div>
              )}

              {mockData.ucl && (
                <div>
                  <div className="text-muted-foreground">UCL</div>
                  <div className="font-semibold font-mono">{mockData.ucl} т/ч</div>
                </div>
              )}

              {mockData.excess && (
                <div>
                  <div className="text-muted-foreground">Превышение</div>
                  <div className="font-semibold text-red-600">
                    +{mockData.excess} т/ч ({((mockData.excess / mockData.ucl!) * 100).toFixed(1)}%)
                  </div>
                </div>
              )}

              {mockData.flowG1 && (
                <div>
                  <div className="text-muted-foreground">Расход G1</div>
                  <div className="font-semibold font-mono">{mockData.flowG1} т/ч</div>
                </div>
              )}

              {mockData.distanceToLimit && (
                <div>
                  <div className="text-muted-foreground">До границы</div>
                  <div className="font-semibold text-yellow-600">+{mockData.distanceToLimit} т/ч</div>
                </div>
              )}

              {mockData.trend && (
                <div>
                  <div className="text-muted-foreground">Тренд</div>
                  <div className="font-semibold text-red-600">↑ {mockData.trend}</div>
                </div>
              )}

              {mockData.duration && (
                <div>
                  <div className="text-muted-foreground">Длительность</div>
                  <div className="font-semibold">{mockData.duration}</div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Actions */}
        <div className="text-sm mb-3">
          <strong>Рекомендации:</strong>
          <ul className="mt-2 ml-6 space-y-1 leading-relaxed">
            {Array.isArray(recommendation.actions) ? 
              recommendation.actions.map((action, index) => (
                <li key={index} className="list-disc">{action}</li>
              )) :
              typeof recommendation.actions === 'string' ? 
                <li className="list-disc">{recommendation.actions}</li> : null
            }
          </ul>
        </div>

        {/* Action Buttons */}
        <div className="flex gap-2">
          <Button 
            onClick={handleAccept}
            disabled={isAccepted}
            data-testid={`button-accept-${recommendation.id}`}
          >
            <ClipboardCheck className="w-4 h-4 mr-2" />
            {isAccepted ? 'Принято в работу' : 'Принять в работу'}
          </Button>
          <Button variant="outline" onClick={handleShowAnalytics}>
            <BarChart3 className="w-4 h-4 mr-2" />
            Подробная аналитика
          </Button>
          <Button variant="outline" onClick={handleShowHistory}>
            <History className="w-4 h-4 mr-2" />
            История
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/tables/ctp-table.t.content:
import { StatusBadge } from "@/components/ui/status-badge";
import { Button } from "@/components/ui/button";
import { Eye } from "lucide-react";
import { cn } from "@/lib/utils";
import type { CTPWithDetails } from "@shared/schema";

interface CTPTableProps {
  data: CTPWithDetails[];
  onRowClick?: (ctp: CTPWithDetails) => void;
}

export function CTPTable({ data, onRowClick }: CTPTableProps) {
  const getStatusInfo = (ctp: CTPWithDetails) => {
    const measurement = ctp.latestMeasurement;
    if (!measurement) return { status: 'normal', label: 'Нет данных' };

    const criticalRec = ctp.recommendations.find(r => r.priority === 'critical');
    const warningRec = ctp.recommendations.find(r => r.priority === 'warning');

    if (criticalRec) {
      return { status: 'critical', label: 'Критично' };
    } else if (warningRec) {
      return { status: 'warning', label: 'Внимание' };
    } else {
      return { status: 'normal', label: 'Норма' };
    }
  };

  const getDeviation = (ctp: CTPWithDetails) => {
    const measurement = ctp.latestMeasurement;
    if (!measurement || !ctp.cl) return '—';

    const deviation = measurement.makeupWater - ctp.cl;
    const sign = deviation > 0 ? '+' : '';
    return `${sign}${deviation.toFixed(1)}`;
  };

  const getRowBackgroundClass = (ctp: CTPWithDetails) => {
    const measurement = ctp.latestMeasurement;
    if (!measurement) return '';

    const isAboveUCL = ctp.ucl && measurement.makeupWater > ctp.ucl;
    const isBelowLCL = ctp.lcl && measurement.makeupWater < ctp.lcl;
    const hasUndermix = measurement.undermix !== null && measurement.undermix < -2;

    if (isAboveUCL) return 'bg-red-50 dark:bg-red-950/20';
    if (isBelowLCL || hasUndermix) return 'bg-blue-50 dark:bg-blue-950/20';
    return '';
  };

  return (
    <div className="overflow-x-auto">
      <table className="data-table">
        <thead>
          <tr>
            <th>ЦТП</th>
            <th>РТС</th>
            <th>Микрорайон</th>
            <th>Подпитка т/ч</th>
            <th>Подмес т/ч</th>
            <th>UCL</th>
            <th>CL</th>
            <th>LCL</th>
            <th>Отклонение</th>
            <th>Статус</th>
            <th>Рекомендация</th>
          </tr>
        </thead>
        <tbody>
          {data.map((ctp) => {
            const measurement = ctp.latestMeasurement;
            const statusInfo = getStatusInfo(ctp);
            const deviation = getDeviation(ctp);
            const rowClass = getRowBackgroundClass(ctp);

            return (
              <tr 
                key={ctp.id} 
                className={cn(rowClass, onRowClick && "cursor-pointer hover:bg-muted/50")}
                onClick={() => onRowClick?.(ctp)}
                data-testid={`ctp-row-${ctp.id}`}
              >
                <td className="font-semibold">{ctp.name}</td>
                <td>{ctp.rts.code}</td>
                <td>{ctp.district.name}</td>
                <td className={cn(
                  "font-mono",
                  statusInfo.status === 'critical' && "font-bold text-red-600",
                  statusInfo.status === 'warning' && "font-semibold text-yellow-600"
                )}>
                  {measurement?.makeupWater.toFixed(1) || '—'}
                </td>
                <td className={cn(
                  "font-mono",
                  measurement && measurement.undermix !== null && measurement.undermix < -2 && "font-bold text-blue-600"
                )}>
                  {measurement?.undermix?.toFixed(1) || '—'}
                </td>
                <td className="font-mono">{ctp.ucl?.toFixed(1) || '—'}</td>
                <td className="font-mono">{ctp.cl?.toFixed(1) || '—'}</td>
                <td className="font-mono">{ctp.lcl?.toFixed(1) || '—'}</td>
                <td className={cn(
                  "font-semibold",
                  statusInfo.status === 'critical' && "text-red-600",
                  statusInfo.status === 'warning' && "text-yellow-600",
                  statusInfo.status === 'normal' && "text-green-600"
                )}>
                  {deviation}
                </td>
                <td>
                  <StatusBadge 
                    status={statusInfo.status as 'normal' | 'warning' | 'critical'}
                  >
                    {statusInfo.label}
                  </StatusBadge>
                </td>
                <td>
                  {ctp.recommendations.length > 0 ? (
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        // Handle recommendation action
                      }}
                      data-testid={`recommendation-${ctp.id}`}
                    >
                      <Eye className="w-3 h-3 mr-1" />
                      {ctp.recommendations[0].type === 'inspection' ? 'Инспекция' :
                       ctp.recommendations[0].type === 'meter_check' ? 'Проверка' : 'Мониторинг'}
                    </Button>
                  ) : '—'}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

client/src/components/tree/tree-node.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { StatusBadge } from "@/components/ui/status-badge";
import { 
  ChevronDown, 
  ChevronRight, 
  Building2, 
  MapPin, 
  Thermometer, 
  Search,
  Eye,
  Settings
} from "lucide-react";
import { cn } from "@/lib/utils";

interface TreeNodeProps {
  id: string;
  icon: 'city' | 'building' | 'map' | 'thermometer';
  name: string;
  makeupWater: number;
  weeklyChange?: number;
  status: 'normal' | 'warning' | 'critical';
  actionType?: string;
  isRoot?: boolean;
  isLeaf?: boolean;
  children?: React.ReactNode;
}

const iconMap = {
  city: Building2,
  building: Building2,
  map: MapPin,
  thermometer: Thermometer,
};

const statusColorMap = {
  normal: 'text-green-600',
  warning: 'text-yellow-600', 
  critical: 'text-red-600',
};

export function TreeNode({ 
  id, 
  icon, 
  name, 
  makeupWater, 
  weeklyChange, 
  status, 
  actionType,
  isRoot = false,
  isLeaf = false,
  children 
}: TreeNodeProps) {
  const [isExpanded, setIsExpanded] = useState(isRoot || !isLeaf);

  const Icon = iconMap[icon];
  const hasChildren = !!children;

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'critical': return 'Критично';
      case 'warning': return 'Внимание';
      default: return 'Норма';
    }
  };

  const getBackgroundClass = (status: string) => {
    switch (status) {
      case 'critical': return 'bg-red-50 border-red-200 dark:bg-red-950/20 dark:border-red-800';
      case 'warning': return 'bg-yellow-50 border-yellow-200 dark:bg-yellow-950/20 dark:border-yellow-800';
      default: return '';
    }
  };

  const toggleExpansion = () => {
    if (hasChildren) {
      setIsExpanded(!isExpanded);
    }
  };

  return (
    <div className="space-y-1">
      <div 
        className={cn(
          "tree-node p-4 cursor-pointer transition-all",
          getBackgroundClass(status),
          isExpanded && hasChildren && "expanded"
        )}
        onClick={toggleExpansion}
        data-testid={`tree-node-${id}`}
      >
        <div className="flex justify-between items-center">
          <div className="flex items-center gap-3">
            {hasChildren && (
              <>
                {isExpanded ? (
                  <ChevronDown className="w-4 h-4 text-muted-foreground transition-transform" />
                ) : (
                  <ChevronRight className="w-4 h-4 text-muted-foreground transition-transform" />
                )}
              </>
            )}
            <Icon className={cn("w-5 h-5", statusColorMap[status])} />
            <span className={cn("font-medium", status === 'critical' && "font-bold")}>
              {name}
            </span>
          </div>

          <div className="flex gap-4 text-sm items-center">
            <span className="text-muted-foreground">
              Подпитка: 
              <strong 
                className={cn(
                  "font-mono ml-1",
                  status === 'critical' && "text-red-600 font-bold",
                  status === 'warning' && "text-yellow-600 font-semibold"
                )}
              >
                {makeupWater.toFixed(1)} т/ч
              </strong>
            </span>

            {weeklyChange !== undefined && (
              <span 
                className={cn(
                  "font-semibold",
                  weeklyChange < 0 ? "text-green-600" : "text-red-600"
                )}
              >
                Δ неделя: <strong>{weeklyChange > 0 ? '+' : ''}{weeklyChange.toFixed(1)} т/ч</strong>
              </span>
            )}

            <StatusBadge status={status}>
              {getStatusLabel(status)}
            </StatusBadge>

            {actionType && (
              <Button 
                variant="outline" 
                size="sm"
                onClick={(e) => {
                  e.stopPropagation();
                  // Handle action click
                }}
                data-testid={`action-${id}`}
              >
                {actionType === 'Инспекция утечек' && <Search className="w-3 h-3 mr-1" />}
                {actionType === 'Мониторинг' && <Eye className="w-3 h-3 mr-1" />}
                {actionType === 'Проверка приборов' && <Settings className="w-3 h-3 mr-1" />}
                {actionType}
              </Button>
            )}
          </div>
        </div>
      </div>

      {hasChildren && isExpanded && (
        <div className="tree-children ml-6 border-l-2 border-border pl-4">
          {children}
        </div>
      )}
    </div>
  );
}

